CREATE TABLE ALUMNO (ID BIGINT NOT NULL, APELLIDO1 VARCHAR(20) NOT NULL, APELLIDO2 VARCHAR(20), CP DOUBLE, DNI VARCHAR(10) NOT NULL UNIQUE, DIRECCION VARCHAR(50), EMAIL_INSTITUCIONAL VARCHAR(40) NOT NULL, EMAIL_PERSONAL VARCHAR(40), LOCALIDAD VARCHAR(40), MOVIL INTEGER, NOMBRE VARCHAR(15) NOT NULL, PROVINCIA VARCHAR(25), TELEFONO INTEGER, USUARIO VARCHAR(40), PRIMARY KEY (ID))
