CREATE TABLE ASIGNATURA (REFERENCIA INTEGER NOT NULL, CARACTER VARCHAR(10), CODIGO INTEGER NOT NULL, CREDITOS_TEORIA FLOAT, CREDITOS_TOTAL FLOAT NOT NULL, CURSO INTEGER, DURACION INTEGER, NOMBRE VARCHAR(30) NOT NULL, OFERTADA VARCHAR(2) NOT NULL, UNIDAD_TEMPORAL VARCHAR(10), TITULACION_CODIGO INTEGER NOT NULL, PRIMARY KEY (REFERENCIA))
